INSERT INTO `singer` VALUES (1584080833451417601, '五月天', 1, 'http://localhost/avatar/65e5d6cea81e09d4b8d73bb17b39af03.jpg', '1982-12-09 00:00:00', '中国大陆', '五月天，台湾摇滚乐团，由乐队团长兼吉他手怪兽（温尚翊）、主唱阿信（陈信宏）、吉他手石头（石锦航）、贝斯玛莎（蔡升晏）和鼓手冠佑（刘谚明）组成，前身为“So Band”乐团，在1997年3月29日更名为“五月天”。至2016年，四次拿下\"台湾金曲奖\"和“最佳乐团奖”。');
