INSERT INTO `song` VALUES (1599001857009770498, 1599001097563922433, '就忘了吧', '就忘了吧', '2022-12-03 19:25:25', '2022-12-06 14:55:46', 'https://www.freemusic.ltd/avatar/f587e30d02b21551d1d7b42ab3646970.jpg', '[00:00.000]就忘了吧 - 1K\n[00:00.110]词：1K\n[00:00.180]曲：1K\n[00:00.240]编曲：1K/方杨\n[00:00.360]制作人：一寸光年团队\n[00:00.520]键盘：1K\n[00:00.610]吉他：潘春宇/1K\n[00:00.750]贝斯：招炳坤\n[00:00.850]和声编写/和声：1K\n[00:01.020]混音：1K\n[00:01.100]母带：全相彦@OK master studio\n[00:01.260]监制：刘通\n[00:01.350]制作公司/OP：一寸光年\n[00:01.510]（未经著作权人许可，不得翻唱翻录或使用）\n[00:01.860]我等了好久你始终都没出现\n[00:05.510]再见一面我可以站得远一点\n[00:09.310]满天 飞雪 把我心冷却\n[00:14.800]想问问你最近过得怎么样\n[00:17.850]有没新的另一半\n[00:19.690]还是和从前一样简单\n[00:23.280]有没有我的世界都一样没波澜\n[00:29.380]世界很大你要到处看看\n[00:31.920]你要到处逛逛只有我在老地方\n[00:37.120]陌生又透着光 回忆里的模样\n[00:40.780]就忘了吧\n[00:45.330]对不起 是我自己不了解\n[00:48.860]给了你一次又一次伤害\n[00:52.470]翻山越岭再也找不到\n[00:55.960]像你一样的存在 不存在\n[00:59.760]分开了一个 春秋又冬夏\n[01:03.450]对你的思念 他还在挣扎\n[01:06.650]我一个人停停走走 找不到出口\n[01:12.840]在那些和你错开的时间里\n[01:16.080]我骗过我自己 以为能忘了你\n[01:20.610]任何事情都让我 不自主地想起\n[01:26.610]所以 我拒绝过几许没办法\n[01:31.990]回避的有些动情\n[01:35.250]无法让别人再成为我的唯一\n[02:11.240]世界很大你要到处看看\n[02:13.770]你要到处逛逛只有我在老地方\n[02:18.950]我能有多倔强 我还能怎么办\n[02:22.600]就忘了吧\n[02:25.580]在那些和你错开的时间里\n[02:28.780]我骗过我自己 以为能忘了你\n[02:33.340]任何事情都让我 不自主地想起\n[02:39.330]所以 我拒绝过几许没办法\n[02:44.670]回避的有些动情\n[02:47.960]无法让别人再成为我的唯一', 'https://www.freemusic.ltd/voice/4ee0bb2c6b7960bce0598bbca41fefcb.mp3', 6);
